import React, { useState, useEffect } from 'react';
import { TreeTable } from 'primereact/treetable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
// import  { NodeService } from './NodeService';
import { NodeService } from './NodeService';
import useFetch from "./useFetch";
import axios from 'axios'
import { InputText } from 'primereact';


const ListItems = (props, {onSelected}) => {
    const [selectedObj, setSelectedObj] = useState(null);
    const [nodes, setNodes] = useState([]);
    const [globalFilter, setGlobalFilter] = useState(null);

    const treeTableFuncMap = {
      'globalFilter': setGlobalFilter
  };

    useEffect(() => {
        axios 
        .get("http://localhost:8000/db")
        .then((res) => setNodes(res.data) )

    }, [])

    const getHeader = (globalFilterKey) => {
      return (
          <div className="text-right">
              <div className="p-input-icon-left">
                  <i className="pi pi-search"></i>
                  <InputText type="search" onInput={(e) => treeTableFuncMap[`${globalFilterKey}`](e.target.value)} placeholder="Global Search" size="50" />
              </div>
          </div>
      );
  }

  let header = getHeader('globalFilter');
    // const passSelected = (value)=> onSelected(value)

    // const selectorHandle = (e) => {
    //   setSelectedObj(e.value)
    //   passSelected(e.value)
    // }


    return (
      <TreeTable value={nodes.root} resizableColumns columnResizeMode="fit" showGridlines sortMode="multiple">
             <Column field="name" header="Name" expander sortable></Column>
            <Column field="size" header="Size" sortable></Column>
            <Column field="type" header="Type" sortable></Column>
        </TreeTable>
    );
}

export default ListItems;
