import { useState } from "react";
import Details from "./Details";
import Items from "./Items";
import Locations from "./Locations";
import NewItem from "./NewItem";
import NPCs from "./NPCs";
import QuestList from "./QuestList";
import Quests from "./Quests";
import RemoveItem from "./RemoveItem";

const MainGrid = () => {
  const [activeQuest, setActiveQuest] = useState();
  const [activeLocation, setActiveLocation] = useState();
  const [activeNPC, setActiveNPC] = useState();
  const [activeItem, setActiveItem] = useState();


  return (
    <>
    <div className="grid">
      <div className="grid mt-3 mx-1 h-auto col-12">
        <div className="col"><Quests activeQuest = {activeQuest}/></div>
        <div className="col"><Locations activeLocation = {activeLocation}/></div>
        <div className="col"><NPCs activeNPC = {activeNPC}/></div>
        <div className="col"><Items activeItem = {activeItem}/></div>
      </div>
      <div className="grid mt-3 mx-1 h-auto col-8">
        <div className="col"><QuestList onSelected={(value) => setActiveQuest(value)}/></div>
        <div className="col">Location List</div>
        <div className="col">NPC List</div>
        <div className="col">Item List</div>
      </div>
      <div className="col max-h-full w-3 mr-6" id="details"><Details quest={activeQuest}/></div>
      </div>


    </>
  );
};

export default MainGrid;
