import { Card } from "primereact/card";
import { useEffect, useRef, useState } from "react";

const Details = (props) => {
  const questParams =
    props.quest === undefined ||
    props.quest === null ||
    props === undefined ||
    props === null 

  const questName = questParams
    ? (props = "No Selection")
    : props.quest.name;

  const questDetail = questParams
    ? (props = "")
    : props.quest.detail;

  const locationParams = 
    props.location === undefined ||
    props.location === null ||
    props === undefined ||
    props === null 

  const locationName = locationParams
    ? (props = "No Selection")
    : props.quest.name;

  const locationDetail = locationParams
    ? (props = "")
    : props.quest.detail;

  const npcParams = 
    props.NPC === undefined ||
    props.NPC === null ||
    props === undefined ||
    props === null 

  const itemParams = 
    props.item === undefined ||
    props.item === null ||
    props === undefined ||
    props === null   



  return (
    <>
      <h1>Details</h1>
      <Card
        title={questName}
        style={{ width: "25rem", marginBottom: "2em" }}
      >
        <p className="m-0" style={{ lineHeight: "1.5" }}>
          {questDetail}
        </p>
      </Card>
    </>
  );
};

export default Details;
